public class GoogleBillboard {private static double answer; public final static String e = "2.718281828459045235360287471352662497757247093699959574966967627724076630353547594571382178525166427427466391932003059";public static void main(String args[]){for ( int i = 2; i < e.length() - 10; i++){String s = e.substring(i, i + 10); double n = Double.parseDouble(s);if (isPrime(n))answer = n;}String number = (""+answer).replace(".","").substring(0,10); System.out.println("The number is: "+number + ".");}public static boolean isPrime(double dNum){for (int i = 2; i < (int)Math.sqrt(dNum); i++)if (dNum % i == 0)return false;return true;}}